(110, '', '', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../tinymce/upload-files/ikc_pravo/123.jpg\" alt=\"\" width=\"650\" height=\"920\" /></p>', '', '', NULL, NULL),
